import React, {useRef, useReducer, useCallback} from 'react';
import UserList from './UserList';
import CreateUser from './CreateUser';
function countActiveUsers(users){
  console.log("사용자 수를 세는중");
  return users.filter(user => user.active).length;
}
const initialState = {
  inputs:{
    usersname:'',
    email:'',
  },
  users:[
    {
      id: 1,
      username: 'Viva',
      email: 'happybird00@naver.com',
      active: true
  },
  {
      id: 2,
      username: 'tester',
      email: 'tester@naver.com',
      active: false
  },
  {
      id: 3,
      username: 'dongwook',
      email: 'wooksker',
      active: true
  }
  ]
}
function reducer(state,action){
  switch(action.type){
    case 'CHANGE_INPUT':
      return{
        ...state,
        inputs:{
          ...state.inputs,
          [action.name]: action.value
        }
      };
      case 'CREATE_USER':
        return{
          inputs: initialState.inputs,
          users: state.users.concat(action.user)
        };
      case 'TOGGLE_USER':
        return{
          ...state,
          user:state.users.map(user=>
            user.id===action.id?{...user,active: !user.active}
            :user)
        };
      case 'REMOVE_USER':
        return {
          ...state,
          user:state.user.filter(user=>user.id !==action.id)
        }
      default:
        throw new Error('Unhandled action');
  }
}

function App() {
  const [state,dispatch] = useReducer(reducer,initialState);
  const {users} = state;
  const nextId= useRef(4);
  const {username, email} = state.inputs;
  const onChange = useCallback(e=>{
    const {name, value}= e.target;
    dispatch({
      type: 'CHANGE_INPUT',
      name,
      value,
    })
  },[]);
  const onCreate = useCallback(()=>{
    dispatch({
      type:'CREATE_USER',
      user:{
        id:nextId.current,
        username,
        email,
      }
    });
    nextId.current +=1;
  },[username,email])
  return (
    <> 
    <CreateUser 
    username={username}
    email={email}
    onChange={onChange}
    onCreate={onCreate}/>
    <UserList users={users}/>
    <div>활성 사용자 수:0</div>
    </>
  );
}

export default App;
