const myLogger = (store) => (next) => (action) => {
  console.log(action);
  //미들웨어에서 액선을 인지

  const result = next(action);
  //인지한 액션을 next로 전달후에 실행

  console.log("\t ", store.getState());
  return result;
  //dispatch의 결과물
};

export default myLogger;
